package JavaTraining;

public class LoopsInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//while loop
		//Disadvantage of while Loop  it will give infinite loop if you don’t give incremental /decrement part
		int i=1;
		while(i<=10) {
			System.out.println(i);
			i++;
		}

		//for loop
		for(int j=1;j<=10;j++){
			System.out.println(j);
		}
	}

}
